'use strict';

let _Parser = require('./Parser.js');
let _Printer = require('./Printer.js');
let _ScopeResolver = require('./ScopeResolver.js');
let _AST = require('./AST.js');
const AST = _AST;
exports.AST = undefined;
exports.parse = undefined;
exports.print = undefined;
exports.resolveScopes = undefined;
exports.AST = AST;
exports.parse = parse;
exports.print = print;
exports.resolveScopes = resolveScopes;

function print(ast, options) {
  return new _Printer.Printer().print(ast, options);
}

function parse(input, options = {}) {
  let parser = new _Parser.Parser(input, options);
  let result = options.module ? parser.parseModule() : parser.parseScript();
  return result;
}

function resolveScopes(ast, options) {
  return new _ScopeResolver.ScopeResolver().resolve(ast, options);
}